{
	Script[ComBot].VariableScope.UI:Update["obj_Hauler", "Unable to verify Pickup bookmark", "r"]
}
Script[ComBot].VariableScope.Hauler.LocalUI:Stop
ui -unload "&filepath;/Hauler"
