	case Corporate Hangar
	case Personal Hangar
		if 	!${EVE.Bookmark[${Script[ComBot].VariableScope.Miner.Config.Dropoff}](exists)}
		{
			Script[ComBot].VariableScope.UI:Update["obj_Miner", "Unable to verify Dropoff bookmark", "r"]
		}
		break
